["Initialization"] = "function (modTable)\n    \n    function modTable.ShowPlayer (unitFrame)\n        \n        -- check if showing guild name on friendly player is enabled\n        if not Plater.db.profile.plate_config.friendlyplayer.show_guild_name then return end\n        -- check if player has a guild name at all\n        if not unitFrame.PlateFrame.playerGuildName then return end\n        -- check if the custom guild nameplate is already being shown\n        if unitFrame.PlateFrame.ActorTitleSpecial:IsShown() then return end\n        \n        -- create custom guild nameplate and set text and color\n        unitFrame.PlateFrame.ActorTitleSpecial:SetText(\"<\" .. unitFrame.PlateFrame.playerGuildName .. \">\")\n        Plater:SetFontFace (unitFrame.PlateFrame.ActorTitleSpecial, modTable.config['textFontFace']);\n        Plater:SetFontSize (unitFrame.PlateFrame.ActorTitleSpecial, modTable.config['textSize']);\n        Plater:SetFontOutline (unitFrame.PlateFrame.ActorTitleSpecial, modTable.config['textOutline']);\n        \n        -- identify if in same guild and color if enabled\n        if (unitFrame.PlateFrame.playerGuildName == Plater.PlayerGuildName) and (modTable.config['uniqueGuildColor']) then\n            Plater:SetFontColor (unitFrame.PlateFrame.ActorTitleSpecial, modTable.config['textColorSameGuild']);\n        else\n            Plater:SetFontColor (unitFrame.PlateFrame.ActorTitleSpecial, modTable.config['textColor']);\n        end\n        \n        -- ensure the default guild nameplate is hidden\n        unitFrame.PlateFrame.playerGuildName = nil\n        Plater.UpdateUnitName (unitFrame.PlateFrame)\n        \n        -- show the custom guild nameplate\n        unitFrame.PlateFrame.ActorTitleSpecial:Show()\n    end\n    \nend",
["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    -- Ensure this only applies to friendly players\n    if UnitIsFriend(\"player\", unitId) and UnitIsPlayer(unitId) then\n        modTable.ShowPlayer (unitFrame)\n    end\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
